	public void ImprimirTabuleiro(String[][] campo){
        String tabuleiro = "";
        for (int x = 0; x < 50; x++) {
        	String num = "";
        	if((x + 1) < 10) {
        		num = "0"+(x+1);
        	} else {
        		num = String.valueOf((x+1));
        	}
            tabuleiro += num+"| ";
            for (int y = 0; y < 50; y++) {
            	String valor = "";
            	if(campo[x][y] != " ") {
            		valor = campo[x][y]; 
            	} else {
            		valor = " ";
            	}
            	tabuleiro += valor+"   ";
            }
            tabuleiro += "\n";
        }
        limpatela();
        System.out.println(tabuleiro);
    }
